    props.setProgress(100);
}, 500);
}, []);

document.body.style.background = "#242124";

const [text, setText] = useState("Enter code here..");

const handleOnChange = (Event) => {
setText(Event.target.value);
};

return (
<div className="container ">
    <div className="row mb-3 my-3">
    <div className="col-sm-4" ><p style={{color:"white"}}>Dropdown</p></div>
    <div className="col">
        <textarea
        name="code"
        autofocus
        className="form-control"
        id="textfield"
        rows="20"
        placeholder="Enter your text here "
        value={text}
        onChange={handleOnChange}
        style={{ background: "black", color: "white" }}
        ></textarea>
        <Buttons marginXaxis="2" marginYaxis="2" name="Run" />
        <Buttons marginXaxis="2" marginYaxis="2" name="Ask AI" />
        <Buttons marginXaxis="2" marginYaxis="2" name="Reset" />
    </div>
    </div>
</div>
);
}

export default Compiler;
