import React, { useEffect, useRef } from 'react';
import SwiperCore, { Swiper } from 'swiper';
import 'swiper/swiper-bundle.min.css';

import image1 from "../images/page1.jpg";
import image2 from "../images/page2.jpg";
import image3 from "../images/page3.jpg";

SwiperCore.use([Parallax]);

export default function CarouselComponent() {
  const parallaxRef = useRef(null);

  useEffect(() => {
    if (parallaxRef.current) {
      parallaxRef.current.update();
    }
  }, []);

  return (
    <Swiper
      parallax={{ mousemove: true }}
      className="mySwiper"
      style={{ width: "90vw", height: "90vh" }}
      onSwiper={(swiper) => (parallaxRef.current = swiper)}
    >
      <div style={{ backgroundImage: `url(${image1})` }} className="swiper-slide">
        <div className="swiper-parallax">
          {/* Your content for the first slide */}
        </div>
      </div>
      <div style={{ backgroundImage: `url(${image2})` }} className="swiper-slide">
        <div className="swiper-parallax">
          {/* Your content for the second slide */}
        </div>
      </div>
      <div style={{ backgroundImage: `url(${image3})` }} className="swiper-slide">
        <div className="swiper-parallax">
          {/* Your content for the third slide */}
        </div>
      </div>
    </Swiper>
  );
}
