import React, { useEffect, useState } from 'react'

function Compiler(props) {
    useEffect(()=>{
        props.setProgress(50)
        setTimeout(()=>{
            props.setProgress(100)
        },1000)
    },[])

document.body.style.background="#242124"

const [text,setText]=useState("Enter code here..")

const handleOnChange=(Event)=>{
    setText(Event.target.value)
}

return (
<>
    <div className='mb-3 my-3' >

        <textarea autofocus className="form-control" id="textfield"  rows="10" placeholder='Enter your text here ' value={text} onChange={handleOnChange} style={{background:"#28282B",color:"white"}}></textarea>
    </div>
</>
)
}

export default Compiler
