import React, { useEffect, useState } from "react";
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';


function Compiler(props) {

const [text, setText] = useState("Enter code here..");

useEffect(() => {
props.setProgress(50);
setTimeout(() => {
    props.setProgress(100);
}, 500);
}, []);

const handleToReset=()=>{
    setText("Enter code here..")
}

document.body.style.background = "#242124";


const handleOnChange = (Event) => {
setText(Event.target.value);
};

const handle1=()=>{
    document.getElementById("button1").style.color="grey"
}
const handleLeave1=()=>{
    document.getElementById("button1").style.color="white"
}
const handle2=()=>{
    document.getElementById("button2").style.color="grey"
}
const handleLeave2=()=>{
    document.getElementById("button2").style.color="black"
}
const handle3=()=>{
    document.getElementById("button3").style.color="grey"
}
const handleLeave3=()=>{
    document.getElementById("button3").style.color="white"
}

return (
<div className="container ">
    <div className="row mb-3 my-3">
    <div className="col-sm-4" >
    <DropdownButton variant="light" id="dropdown-item-button" title="Language">
    <Dropdown.Item as="button">C</Dropdown.Item>
    <Dropdown.Item as="button">C++</Dropdown.Item>
    <Dropdown.Item as="button">Java</Dropdown.Item>
    <Dropdown.Item as="button">Javascript</Dropdown.Item>
    <Dropdown.Item as="button">Python</Dropdown.Item>
    </DropdownButton>
    </div>
    <div className="col">
        <textarea
        name="code"
        autofocus
        className="form-control"
        id="textfield"
        rows="20"
        placeholder="Enter your text here "
        value={text}
        onChange={handleOnChange}
        style={{ background: "black", color: "white" }}
        ></textarea>
            <button id="button1" className="btn btn-primary  my-2 mx-2" onMouseLeave={handleLeave1} onMouseOver={handle1} style={{backgroundColor:"#228B22",borderColor:"green"}}>Run</button>
            <button id="button2" className="btn btn-primary my-2 mx-2" onMouseLeave={handleLeave2} onMouseOver={handle2} style={{backgroundColor:"white",color:"black"}} onClick={handleToReset}>Clear</button>
            <button id="button3" className="btn btn-primary my-2 mx-2" onMouseLeave={handleLeave3} onMouseOver={handle3} >Ask AI</button>
    </div>
    </div>
</div>
);
}

export default Compiler;
