import React, { useEffect, useState } from "react";

function Compiler(props) {

const [text, setText] = useState("Enter code here..");

useEffect(() => {
props.setProgress(50);
setTimeout(() => {
    props.setProgress(100);
}, 500);
}, []);

const handleToReset=()=>{
    setText("Enter code here..")
}

document.body.style.background = "#242124";


const handleOnChange = (Event) => {
setText(Event.target.value);
};

const handle=()=>{
    document.getElementsByClassName("button").style.color="grey"
}
const handleLeave=()=>{
    document.getElementById("button2").style.color="white"
}

return (
<div className="container ">
    <div className="row mb-3 my-3">
    <div className="col-sm-4" ><p style={{color:"white"}}>Dropdown</p></div>
    <div className="col">
        <textarea
        name="code"
        autofocus
        className="form-control"
        id="textfield"
        rows="20"
        placeholder="Enter your text here "
        value={text}
        onChange={handleOnChange}
        style={{ background: "black", color: "white" }}
        ></textarea>
            <button className="button btn btn-primary  my-2 mx-2" onMouseLeave={handleLeave} onMouseOver={handle}>Run</button>
            <button  className="button btn btn-primary my-2 mx-2" onMouseLeave={handleLeave} onMouseOver={handle} onClick={handleToReset}>Clear</button>
            <button className="button btn btn-primary my-2 mx-2" onMouseLeave={handleLeave} onMouseOver={handle}>Ask AI</button>
    </div>
    </div>
</div>
);
}

export default Compiler;
