const [text, setText] = useState("Enter code here..");

const handleOnChange = (Event) => {
setText(Event.target.value);
};

return (
<div className="container ">
    <div className="row mb-3 my-3">
    <div className="col" ><p style={{color:"white"}}>Dropdown</p></div>
    <div className="col">
        <textarea
        name="code"
